mapper.select:
Select company.company_name
from Company company, Users user
where company.company_id=user.company_id and user.user_id = #{user_id};

Select company.company_id
from Company company, Users user
where company.company_id=user.company_id and user.user_id = #{user_id};

SELECT post.post_id,post.user_id, user.user_name,post.board_id,post.company_id,post.post_title,post.post_content,post.post_register_time
FROM Post post, Users user
WHERE post.user_id = user.user_id and post.board_id=#{board_id}
ORDER BY post.post_id DESC;

SELECT post.post_id,post.user_id, user.user_name,post.board_id,post.company_id,post.post_title,post.post_content,post.post_register_time
FROM Post post, Users user
WHERE post.user_id = user.user_id and post.post_id=#{post_id}

SELECT user_type
FROM Users
WHERE user_id = #{user_id};
